<template>
  <p>自2021年1月1日起已过去 {{ daysPassed }} 天。</p>
</template>

<script>
export default {
  data() {
    return {
      daysPassed: 0
    };
  },
  mounted() {
    const startDate = new Date('2021-01-01');
    const today = new Date();
    const timeDifference = today - startDate;
    this.daysPassed = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
  }
};
</script>

<style scoped>
p {
  font-size: 16px;
  color: #333;
}
</style>
